import Api from "../helpers/api";

export default {
    data(){
        return{
            api: new Api(),
            user: null,
            userAffectations: [],
            userEntites: [],
            userRamifications: [],
            userPermissions: [],

        }

    },
    methods:{
        getUserAffaction(){
            this.userAffectations =  this.user.affectations
        },
        getUserRamification(){
            this.userAffectations.forEach(affectation => {
                if (affectation.ramification !== null){
                    this.api.listing("/ramifications/"+affectation.ramification.id+"/childs")
                        .then(response => {
                            response.data.forEach(item => {
                                this.userRamifications.push(item);
                            })

                        })
                }

            })

        }

    },
    created() {
        this.user = this.$store.state.currentUser;
        this.getUserAffaction();
        this.getUserRamification();

    }
}
