import Select2 from 'vue3-select2-component';
import Api from "../helpers/api";
export default {
  components: {
    Select2
  },
  data() {
    return {
      api: new Api(),
      fields: [],
      typeRamifications: [],
      ramification: null
    };
  },
  created() {
    this.api.listing("/type_ramifications", this.fields, "tram.ordre", "asc", 1000, 1).then(response => {
      this.typeRamifications = response.data;
    });
  }
};
